import OpenAI from "openai";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key" 
});

export interface ResearchSource {
  title: string;
  url: string;
  content: string;
  type: 'file' | 'web' | 'academic';
  confidence: number;
  publishedDate?: string;
}

export interface Citation {
  id: string;
  source: ResearchSource;
  relevance: number;
  excerpt: string;
}

export interface ResearchReport {
  title: string;
  executiveSummary: string;
  keyInsights: string[];
  sources: ResearchSource[];
  citations: Citation[];
  confidence: number;
}

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
export async function generateResearchReport(
  query: string,
  sources: ResearchSource[]
): Promise<ResearchReport> {
  try {
    const sourcesText = sources.map((source, index) => 
      `[${index + 1}] ${source.title}\nType: ${source.type}\nURL: ${source.url}\nContent: ${source.content.substring(0, 2000)}...\n\n`
    ).join('');

    const prompt = `You are an expert research analyst. Based on the following query and sources, generate a comprehensive research report.

Query: "${query}"

Sources:
${sourcesText}

Generate a detailed research report with the following structure. Respond with JSON in this exact format:
{
  "title": "Report title based on the query",
  "executiveSummary": "2-3 sentence executive summary",
  "keyInsights": ["insight 1", "insight 2", "insight 3"],
  "sources": [{"title": "source title", "url": "source url", "type": "source type", "confidence": 0.95}],
  "citations": [{"id": "1", "sourceIndex": 0, "relevance": 0.9, "excerpt": "relevant excerpt from source"}],
  "confidence": 0.92
}

Ensure all insights are backed by the provided sources and include proper citations.`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are an expert research analyst who generates evidence-based reports with proper citations. Always respond with valid JSON."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");

    // Process and enhance the result
    const processedCitations = result.citations?.map((citation: any) => ({
      id: citation.id,
      source: sources[citation.sourceIndex] || sources[0],
      relevance: citation.relevance || 0.8,
      excerpt: citation.excerpt || ""
    })) || [];

    return {
      title: result.title || `Research Report: ${query}`,
      executiveSummary: result.executiveSummary || "No executive summary generated.",
      keyInsights: result.keyInsights || [],
      sources: sources,
      citations: processedCitations,
      confidence: result.confidence || 0.8
    };
  } catch (error) {
    throw new Error(`Failed to generate research report: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

export async function extractTextFromDocument(content: string, mimeType: string): Promise<string> {
  try {
    const prompt = `Extract and clean the main text content from this document. Remove any formatting artifacts, headers, footers, or irrelevant content. Return only the cleaned text content.

Document content:
${content.substring(0, 8000)}`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are a document processing expert. Extract clean, readable text from documents while preserving important information."
        },
        {
          role: "user",
          content: prompt
        }
      ],
    });

    return response.choices[0].message.content || content;
  } catch (error) {
    console.error('Error extracting text:', error);
    return content; // Fallback to original content
  }
}

export async function summarizeContent(content: string, maxLength = 500): Promise<string> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: `Summarize the following content in approximately ${maxLength} characters while maintaining key information and insights.`
        },
        {
          role: "user",
          content: content.substring(0, 4000)
        }
      ],
    });

    return response.choices[0].message.content || content.substring(0, maxLength);
  } catch (error) {
    console.error('Error summarizing content:', error);
    return content.substring(0, maxLength);
  }
}

server/pathway.ts.content:
import { spawn, ChildProcess } from 'child_process';
import { writeFileSync, readFileSync, existsSync } from 'fs';
import path from 'path';
import { storage } from './storage';

export interface PathwayDataItem {
  id: string;
  title: string;
  content: string;
  url: string;
  timestamp: Date;
  source: string;
}

export class PathwayIntegration {
  private pythonProcess: ChildProcess | null = null;
  private isRunning = false;
  private dataFile = path.join(process.cwd(), 'pathway_data.json');
  private scriptFile = path.join(process.cwd(), 'pathway_script.py');

  constructor() {
    this.initializePythonScript();
  }

  private initializePythonScript() {
    const pythonScript = `
import json
import time
import sys
from datetime import datetime, timedelta
import random

# Mock Pathway integration - simulates live data ingestion
class MockPathwayConnector:
    def __init__(self):
        self.sources = [
            {
                "name": "Tech News",
                "type": "news",
                "base_urls": ["https://techcrunch.com", "https://arstechnica.com"]
            },
            {
                "name": "Market Data",
                "type": "market",
                "base_urls": ["https://marketwatch.com", "https://bloomberg.com"]
            }
        ]
        self.data_items = []
        self.counter = 0

    def generate_mock_data(self):
        """Generate mock live data updates"""
        news_titles = [
            "AI Productivity Tools See 340% Growth in Enterprise Adoption",
            "OpenAI Releases New GPT-5 Model with Enhanced Reasoning",
            "Startup Funding Reaches Record Highs in Q1 2024",
            "Microsoft Integrates AI Assistants Across Office Suite",
            "Google's Gemini Model Shows Breakthrough in Code Generation"
        ]

        market_titles = [
            "Tech Stocks Rally on AI Investment News",
            "Venture Capital Focuses on AI Infrastructure",
            "SaaS Companies Report Strong Q1 Earnings",
            "Cloud Computing Revenue Exceeds Projections",
            "Cybersecurity Spending Increases 45% Year-over-Year"
        ]

        content_snippets = [
            "Recent market analysis shows unprecedented growth in AI-powered tools, with enterprise adoption increasing by 340% year-over-year. Companies are investing heavily in automation and productivity enhancement solutions.",
            "The latest developments in artificial intelligence are transforming how businesses operate, with new models offering enhanced capabilities for content generation, data analysis, and process automation.",
            "Investment patterns indicate a strong preference for AI and automation technologies, with funding rounds averaging 60% higher than previous quarters for companies in this sector.",
            "Integration of AI assistants into existing software ecosystems is accelerating, providing users with intelligent automation capabilities across multiple platforms and workflows.",
            "Market research indicates that companies implementing AI productivity tools are seeing average efficiency gains of 25-40% across various operational metrics."
        ]

        source = random.choice(self.sources)
        title = random.choice(news_titles if source["type"] == "news" else market_titles)
        content = random.choice(content_snippets)

        return {
            "id": f"pathway_{self.counter}_{int(time.time())}",
            "title": title,
            "content": content,
            "url": f"{random.choice(source['base_urls'])}/article/{self.counter}",
            "timestamp": datetime.now().isoformat(),
            "source": source["name"]
        }

    def run_continuous(self):
        """Simulate continuous data ingestion"""
        print("Starting Pathway mock integration...", file=sys.stderr)

        while True:
            try:
                # Generate new data every 30-60 seconds
                time.sleep(random.randint(30, 60))

                new_item = self.generate_mock_data()
                self.counter += 1

                # Write to data file for Node.js to read
                try:
                    with open("pathway_data.json", "r") as f:
                        existing_data = json.load(f)
                except (FileNotFoundError, json.JSONDecodeError):
                    existing_data = []

                existing_data.append(new_item)

                # Keep only last 100 items
                if len(existing_data) > 100:
                    existing_data = existing_data[-100:]

                with open("pathway_data.json", "w") as f:
                    json.dump(existing_data, f, indent=2)

                print(f"Generated new data item: {new_item['title']}", file=sys.stderr)

            except Exception as e:
                print(f"Error in Pathway integration: {e}", file=sys.stderr)
                time.sleep(5)

if __name__ == "__main__":
    connector = MockPathwayConnector()
    connector.run_continuous()
`;

    writeFileSync(this.scriptFile, pythonScript);
  }

  async start(): Promise<void> {
    if (this.isRunning) {
      console.log('Pathway integration already running');
      return;
    }

    try {
      // Initialize data file
      if (!existsSync(this.dataFile)) {
        writeFileSync(this.dataFile, '[]');
      }

      // Start Python process
      this.pythonProcess = spawn('python3', [this.scriptFile], {
        stdio: ['ignore', 'ignore', 'pipe']
      });

      this.pythonProcess.stderr?.on('data', (data) => {
        console.log('Pathway:', data.toString().trim());
      });

      this.pythonProcess.on('exit', (code) => {
        console.log(`Pathway process exited with code ${code}`);
        this.isRunning = false;
        // Auto-restart after 5 seconds
        setTimeout(() => this.start(), 5000);
      });

      this.isRunning = true;
      console.log('Pathway integration started successfully');

      // Update live data sources in database
      await this.updateLiveDataSources();
    } catch (error) {
      console.error('Failed to start Pathway integration:', error);
      // Fallback: generate initial mock data
      this.generateFallbackData();
    }
  }

  private generateFallbackData() {
    const mockData = [
      {
        id: "fallback_1",
        title: "AI Productivity Tools Market Analysis",
        content: "Latest market research indicates significant growth in AI-powered productivity solutions, with enterprise adoption increasing substantially across various sectors.",
        url: "https://example.com/ai-productivity-analysis",
        timestamp: new Date().toISOString(),
        source: "Tech News"
      },
      {
        id: "fallback_2",
        title: "Venture Capital Investment Trends",
        content: "Recent funding patterns show increased investment in AI and automation technologies, with venture capital firms focusing on productivity and efficiency solutions.",
        url: "https://example.com/vc-investment-trends",
        timestamp: new Date().toISOString(),
        source: "Market Data"
      }
    ];

    writeFileSync(this.dataFile, JSON.stringify(mockData, null, 2));
  }

  async getLiveData(): Promise<PathwayDataItem[]> {
    try {
      if (!existsSync(this.dataFile)) {
        return [];
      }

      const data = JSON.parse(readFileSync(this.dataFile, 'utf8'));
      return data.map((item: any) => ({
        ...item,
        timestamp: new Date(item.timestamp)
      }));
    } catch (error) {
      console.error('Error reading live data:', error);
      return [];
    }
  }

  async getRecentData(minutes = 60): Promise<PathwayDataItem[]> {
    const allData = await this.getLiveData();
    const cutoff = new Date(Date.now() - minutes * 60 * 1000);

    return allData.filter(item => item.timestamp > cutoff);
  }

  private async updateLiveDataSources(): Promise<void> {
    try {
      const sources = await storage.getLiveDataSources();

      // Update existing sources or create if they don't exist
      const techNewsExists = sources.find(s => s.name === 'Tech News');
      const marketDataExists = sources.find(s => s.name === 'Market Data');

      if (!techNewsExists) {
        await storage.createLiveDataSource(
          'Tech News',
          'news',
          'https://techcrunch.com',
          { pathwayIntegrated: true }
        );
      }

      if (!marketDataExists) {
        await storage.createLiveDataSource(
          'Market Data',
          'market',
          'https://bloomberg.com',
          { pathwayIntegrated: true }
        );
      }
    } catch (error) {
      console.error('Error updating live data sources:', error);
    }
  }

  stop(): void {
    if (this.pythonProcess) {
      this.pythonProcess.kill();
      this.pythonProcess = null;
    }
    this.isRunning = false;
  }
}

// Singleton instance
export const pathwayIntegration = new PathwayIntegration();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

